import React, { useState, useEffect } from 'react';
import SignUp from './SignUp';
import SignIn from './SignIn'
import Dash from '../Dashboard/dash'
import firebase from '../config/fbConfig'





 function Loggedstatus() {
  const [Logstat, setLogstat] = useState(true)
  const [userisLoggedIn, setuserisLoggedIn] = useState(false)


  // useEffect(() => {
  //   setuserisLoggedIn(JSON.parse(window.localStorage.getItem('userisLoggedIn')));
  // }, []);


  // console.log(userisLoggedIn)



  // // const UserLogIn = (event) => {
  // //   event.preventDefault()

  //     var user = firebase.auth().currentUser;
  //     if (user) { 
  //        console.log("User is signed in.")

  //       // User is signed in.
  //     } else {

  //       console.log("No user is signed in.")
  //       // No user is signed in.
  //     }
  // // }

    firebase.auth().onAuthStateChanged(function(user) {
        if (user) {
          console.log("User is signed in.")
          // User is signed in.
        } else {
          // User is not signed in.
        }
    });






    if( !userisLoggedIn) {

      useEffect(() => {
        window.localStorage.setItem('userisLoggedIn', userisLoggedIn);
      }, [userisLoggedIn]);

      if(Logstat) {
        return  (
          < SignIn userisLoggedIn={userisLoggedIn} setuserisLoggedIn={setuserisLoggedIn} Logstat={Logstat} setLogstat={setLogstat}/>
        )
        } else {
              return  (
                < SignUp Logstat={Logstat} setLogstat={setLogstat}/>
              )
        }

    }else{

      useEffect(() => {
        window.localStorage.setItem('userisLoggedIn', userisLoggedIn);
      }, [userisLoggedIn]);


      console.log("User is Signed in then render the dashboard")
      return  (
        < Dash />
      )
    }

  }

  export default Loggedstatus;
